       const update = async (req, res) => {
  try {
    const updateUser = await User.findByIdAndUpdate(
      req.user.id,
      {
        $set: req.body,
      },
      { new: true }
    );

    res.status(200).json({
      message: "User Profile updated succesfully!",
      data: updateUser,
    });
  } catch (error) {
    res.status(500).json(error);
  }
};

